  --github: #32506d;
  --blue: #002266;
  --l-grey: #939393;
  --gold: #665600;
  --font-main: "Montserrat", sans-serif;
  --font-sec: "Charm", cursive;
}

* {
  margin: 0;
  padding: 0;
}

*,
*::before,
*::after {
  box-sizing: inherit;
}

html {
  box-sizing: border-box;
  font-size: 62.5%;
}
@media only screen and (max-width: 1007px) {
  html {
    font-size: 57%;
  }
}
@media only screen and (max-width: 830px) {
  html {
    font-size: 50%;
  }
}

body {
  font-family: var(--font-main);
  font-weight: 400;
  line-height: 1.6;
  overflow-y: scroll;
  scrollbar-width: none;
  /* Firefox */
  -ms-overflow-style: none;
}

body::-webkit-scrollbar {
  /* WebKit */
  width: 0;
  height: 0;
}

@keyframes slide {
  from {
    opacity: 0;
    visibility: visible;
  }
  to {
    opacity: 1;
  }
}
.slide {
  animation: slide 0.1s;
}

.hide {
  animation: hide 2s ease;
}

@keyframes hide {
  from {
    opacity: 1;
  }
  to {
    opacity: 0.4;
  }
}
.morph {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  z-index: 999;
  pointer-events: none;
  transform: translateY(-0.1rem);
}

.a {
  position: relative;
}

@keyframes movelink {
  from {
    opacity: 0;
    transform: translateX(-15rem);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}
