.bg-video {
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  z-index: -1;
  overflow: hidden;
}
.bg-video__content {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.header__index {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  min-height: 100vh;
  background-color: rgba(0, 0, 0, 0.3);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}
.header__index__grid {
  width: 96%;
  min-height: 100vh;
  display: grid;
  grid-template-columns: 4rem 5rem repeat(3, 1fr) 5rem 4rem;
  grid-template-rows: 14rem repeat(6, 1fr);
}

.index-text {
  grid-column: 3/6;
  grid-row: 2/4;
  padding: 0 2rem;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: space-between;
}
.index-text__h1--1 {
  font-size: 4.6rem;
  color: var(--white);
  font-weight: 400;
  text-transform: uppercase;
  line-height: 1;
  padding: 0;
}
.index-text__h1--2 {
  font-size: 4.6rem;
  color: var(--white);
  font-weight: 800;
  text-transform: uppercase;
}

.index-btn__wrapper {
  grid-column: 3/5;
  grid-row: 5/7;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: space-between;
  padding: 0 2rem 2rem 2rem;
}
.index-btn__wrapper__text {
  font-family: var(--font-sec);
  font-size: 2.7rem;
  color: #aca5a5;
  letter-spacing: 0.4rem;
}
.index-btn__wrapper__text span {
  font-family: var(--font-main);
  text-transform: uppercase;
  font-size: 2.1rem;
  color: var(--white);
}
.index-btn__wrapper__box {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 80%;
}
.index-btn__wrapper__box a {
  color: var(--white);
  font-size: 1.6rem;
  text-transform: uppercase;
  padding: 1rem 3.5rem;
  border: 2px solid var(--white);
  text-decoration: none;
  display: inline-block;
  letter-spacing: 0.3rem;
}

.build {
  grid-column: 1/2;
  grid-row: 2/5;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}
.build__h4 {
  color: #fff;
  font-size: 1.4rem;
  text-transform: uppercase;
  writing-mode: vertical-lr;
  flex: 1;
  letter-spacing: 0.4rem;
}

.frontend {
  grid-column: 1/2;
  grid-row: 5/7;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}
.frontend__h4 {
  color: #fff;
  font-size: 1.4rem;
  text-transform: uppercase;
  writing-mode: vertical-lr;
  text-orientation: upright;
  flex: 1;
  letter-spacing: 0.2rem;
}

.rotate {
  grid-column: 5/6;
  grid-row: 4/7;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
}
.rotate__wrapper {
  width: 80%;
  height: 80%;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  transform: rotate(-45deg);
}
.rotate__wrapper__text {
  font-size: 1.6rem;
  color: #fff;
  text-transform: uppercase;
}
.rotate__wrapper__text--2 {
  font-size: 1.3rem;
  color: #fff;
}

.icons {
  grid-column: 7/-1;
  grid-row: 4/7;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  position: relative;
}

.icons__li {
  flex: 1;
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
}

.icons__li__a {
  text-decoration: none;
  font-size: 1rem;
}

.icons__li__a__svg {
  fill: #fff;
  width: 1.8rem;
  height: 1.8rem;
}

.icons__li.phone::before {
  content: "099-786-943";
  position: absolute;
  top: 0;
  left: 0;
  width: 0;
  transform: scaleY(0);
  height: 100%;
  background-color: #000;
  color: #fff;
  font-size: 1.6rem;
  text-transform: uppercase;
  writing-mode: vertical-lr;
  text-orientation: upright;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s linear;
}

.icons__li.phone:hover::before {
  width: 100%;
  transform: scaleY(1);
}

.icons__li.mail::before {
  content: "martingorupic@gmail.com";
  position: absolute;
  top: 0;
  right: 0;
  width: 0;
  transform: scaleY(0);
  height: 100%;
  background-color: #000;
  color: #fff;
  font-size: 1rem;
  writing-mode: vertical-lr;
  text-orientation: upright;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s linear;
  line-height: 1;
}

.icons__li.mail:hover::before {
  width: 100%;
  height: 120%;
  transform: scaleY(1);
}

.text {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  align-items: center;
  justify-content: center;
  font-size: 6rem;
  color: #fff;
  display: none;
  z-index: 5000;
  text-transform: uppercase;
}

