.nav__btn {
  width: 5rem;
  height: 3rem;
  position: fixed;
  top: 5rem;
  right: 3rem;
  z-index: 10;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
}

.bar {
  width: 100%;
  height: 0.4rem;
  background-color: #fff;
}

.bar2 {
  width: 75%;
  align-self: flex-end;
  transition: all 0.6s ease;
}

.nav__btn:hover .bar2 {
  width: 100%;
  transform: translateX(-2rem);
}

.nav__overlay {
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: #e0d4d4;
  z-index: 5;
  -webkit-clip-path: circle(42.9% at 50% 50%);
  clip-path: circle(42.9% at 50% 50%);
  height: 100vh;
  width: 100%;
  background: radial-gradient(ellipse at center, black 11%, black 15%, black 55%, #3c3c3c 60%, white 76%, black 88%);
  /* w3c */
  display: none;
}

.nav__ul {
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 6;
  height: 60vh;
  width: 30%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  display: none;
  overflow: hidden;
}
.nav__ul__li {
  list-style: none;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.nav__a {
  text-decoration: none;
  font-size: 3.2rem;
  color: #fff;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem 0;
}

.nav__ul__li::before {
  content: "";
  position: absolute;
  top: 50;
  left: 0;
  width: 0;
  height: 0.5rem;
  background-color: #fff;
  transform: scaleY(0);
  transition: all 0.4s ease-in-out;
  pointer-events: none;
}

.nav__ul__li:hover::before {
  width: 100%;
  transform: scaleY(1);
}

.block {
  display: flex;
}

.scale {
  transform: scale(1);
}

.toogle .bar1 {
  transform: rotate(-45deg) translateY(1.7rem);
}

.toogle .bar2 {
  opacity: 0;
}

.toogle .bar3 {
  transform: rotate(45deg) translateY(-1.7rem);
}

.bar1,
.bar3 {
  transition: all 0.6s ease-in-out;
  transform-origin: center;
}
